const { Header, Content } = Layout;
const { Meta } = Card;
const gf = new GiphyFetch('UWKAAuSwBXr73cXPrAna6sPY5Z1Agtby');
const GIPHY_API = "https://api.giphy.com/v1/gifs/search?api_key=dc6zaTOxFJmzC&limit=20&offset=0&q=";
const content = (
    <div>
        <div>
            <div className={'pop-over-title'}>
               <strong>
                   Choose Gif
               </strong>
            </div>
            <div>
                <Input className={'gif-search-bar'} autoFocus={true} type={'text'} placeholder={'Search'}/>
            </div>
        </div>
        <Divider/>
        <div>
            <Card cover={<img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />}/>
            <Card cover={<img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />}/>
        </div>

    </div>
);

function App() {
    const [visible, setVisible]= useState(false);
    const [gifVisible, setGifVisible]= useState(false);
const openModal = () => {
    setVisible(true)
};
const handleCancel = () => {
    setVisible(false)
}
const openGifModal = () => {
    setGifVisible(true)
}
const fetchGif = async () => {
    const fetchGifs = (offset: 10) => gf.trending({ offset, limit: 10 });
    const { data } = await gf.gif("fpXxIjftmkk9y");
    console.log('fetchGif', fetchGifs);
    console.log('data', data);
    let searchGif = () => {
            fetch(GIPHY_API+'hello')
            .then((res)=>{
                console.log('res', res);
                return res.json();
            })
            .catch(()=>{
                alert("Something went wrong");
            });
    }
}
    return (
        <Layout>
            <Header className="nav-header">
                <div>
