import { VideoCameraFilled, FileImageFilled, SmileFilled, CloseOutlined } from '@ant-design/icons';

const { Header, Content } = Layout;
const { Meta } = Card;
const text = <span>Title</span>;
const content = (
    <div>
        <p>Content</p>
        <p>Content</p>
    </div>
);

function App() {
    const [visible, setVisible]= useState(false);
    const [gifVisible, setGifVisible]= useState(false);
const openModal = () => {
    console.log("onclick");
