import '../App.css';
import '../styles/style.scss';
import React, { useState } from 'react';
import { Layout, Card, Avatar, Button, Divider, Modal, Input, Spin, Typography } from 'antd';
import {
    VideoCameraFilled,
    FileImageFilled,
    SmileFilled,
    CloseOutlined
} from '@ant-design/icons';
import { GiphyFetch } from '@giphy/js-fetch-api';

const { Header, Content } = Layout;
const { Meta } = Card;
const gf = new GiphyFetch('UWKAAuSwBXr73cXPrAna6sPY5Z1Agtby');
const { Text } = Typography;
const bgColors= ['ED4634', '7A3A24', 'B3B5FD', 'B3B5FD', 'B3B5FD', 'B3B5FD'];
function App() {
    const [visible, setVisible]= useState(false),
        [gifLoader, setGifLoader]= useState(false),
        [gif, setGif]= useState([]),
        [searchedGif, setSearchedGif]= useState([]),
        [selectedGif, SetSelectedGif]= useState([]),
        [searchModalVisible, SetSearchModalVisible]= useState(false),
        [value, setValue]= useState(''),
        openModal = () => {
        setVisible(true)
        },
        handleCancel = () => {
        setVisible(false);
        SetSelectedGif([]);
        },
        fetchGif = async () => {
        SetSearchModalVisible(true);
        setGifLoader(true);
        const response= await gf.trending({ offset:20, limit: 20 });
        setGif(response.data);
        setGifLoader(false);
        },
        searchGif = async (e) => {
        setValue(e.target.value);
        setGifLoader(true);
        const response = await gf.search(`${e.target.value}`, { offset: 20, limit: 20 });
        setSearchedGif(response.data);
        setGifLoader(false);
        },
        clickedGif = (event) => {
        SetSearchModalVisible(false);
        SetSelectedGif(event.target.src);
        setValue('');
        setSearchedGif([]);
        },
        cancelSearch = () => {
        setValue('');
        setSearchedGif([]);
        SetSearchModalVisible(false);
        },
        post = () => {
        console.log('post');
        },
        setBgColor = (index) => {
        console.log('index', index)
        };
    return (
        <Layout>
            <Header className="nav-header">
                <div>
                    <strong>
                        Facebook
                    </strong>
                </div>
                <div className={'account-profile'}>
                    <Avatar src="https://joeschmoe.io/api/v1/random" />
                </div>
            </Header>
            <Content  className={'site-layout '}>
                <div className={'user-story'}>
                        <Card
                            hoverable
                            cover={<img alt="example" src="https://joeschmoe.io/api/v1/random" />}>
                            <span>
                                <svg height="10" width="10">
                                    <circle cx="22" cy="60" r="15" stroke="#fff" stroke-width="5" fill="#1876F2" />
                                    <text x="15" y="67" fill="#fff" font-size="22" font-weight = '600'>+</text>
                                </svg>
                            </span>
                            <Meta title="Create Story" />
                        </Card>
                        <Card hoverable>
                            <img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />
                        </Card>
                        <Card hoverable>
                            <img alt="example"src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />
                        </Card>
                        <Card hoverable>
                            <img alt="example" src="https://gw.alipayobjects.com/zos/antfincdn/x43I27A55%26/photo-1438109491414-7198515b166b.webp" />
                        </Card>
                </div>
                <div className ={'content-post'}>
                <Card className={'post-card'}>
                    <div>
                        <Avatar src="https://joeschmoe.io/api/v1/random" />
                        <Button className={'modal-button'} onClick={openModal}> What's on your mind?</Button>
                    </div>
                    <Divider />
                    <div className={'upload-media'}>
                        <span>
                            <VideoCameraFilled />
                            <Text type="secondary"> Live Video</Text>
                        </span>
                        <span>
                            <FileImageFilled />
                            <Text type="secondary"> Photo/Video</Text>
                        </span>
                        <span>
                            <SmileFilled />
                            <Text type="secondary"> Feeling/Activity</Text>
                        </span>
                    </div>
                </Card>
                    <Modal
                        visible={visible}
                        title="Create Post"
                        className={'post-modal'}
                        closeIcon={<CloseOutlined />}
                        onCancel={handleCancel}
                        footer={false}
                    >
                        <div>
                            <div>
                            <span>
                                <Avatar src="https://joeschmoe.io/api/v1/random" />
                                </span>
                                <span>
                             <strong>
                                 Logeshwaran
                             </strong>
                            </span>
                            </div>
                            <div>
                                <Input
                                    autoFocus={true}
                                    className={'input-field'}
                                    type='text'
                                    placeholder={'Type something here'}
                                />
                            </div>
                            <div className={'bgColors'}>
                                {
                                    bgColors.map((bg, index)=>
                                    <div onClick={setBgColor} key={index} style={{backgroundColor: `#${bg}`}}/>
                                    )

                                }
                                <div className="text-color blue5-color" value="010864"></div>
                                <div className="text-color blue6-color" value="01044C"></div>
                                <div className="text-color pink1-color" value="FDB4CC"></div>
                                <div className="text-color pink2-color" value="FD6899"></div>
                                <div className="text-color pink3-color" value="FC3878"></div>
                                <div className="text-color pink4-color" value="E30E4E"></div>
                                <div className="text-color pink5-color" value="970634"></div>
                                <div className="text-color pink6-color" value="7E042B"></div>
                                <div className="text-color orange1-color" value="FDA36C"></div>
                                <div className="text-color orange2-color" value="FD9455"></div>
                                <div className="text-color orange3-color" value="FD752D"></div>
                                <div className="text-color orange4-color" value="E45B1C"></div>
                                <div className="text-color orange5-color" value="B14714"></div>
                                <div className="text-color orange6-color" value="973D0F"></div>
                                <div className="text-color yellow1-color" value="FFFD72"></div>
                                <div className="text-color yellow2-color" value="FFFD5D"></div>
                                <div className="text-color yellow3-color" value="FFFD3F"></div>
                                <div className="text-color yellow4-color" value="C9CA2A"></div>
                                <div className="text-color yellow5-color" value="B0B224"></div>
                                <div className="text-color yellow6-color" value="97981D"></div>
                                <div className="text-color green1-color" value="B4E4B5"></div>
                                <div className="text-color green2-color" value="8FD790"></div>
                                <div className="text-color green3-color" value="6BC96C"></div>
                                <div className="text-color green4-color" value="59C25A"></div>
                                <div className="text-color green5-color" value="39973A"></div>
                                <div className="text-color green6-color" value="2A712B"></div>
                            </div>
                            {
                                selectedGif.length ?
                                    <div>
                                       <img alt="Gif" className={'post-gif'} src={selectedGif}/>
                                   </div>
                                    :
                                    (
                                        ''
                                    )
                            }
                        </div>
                        <div className={'gif-button'}>
                            <Button type={'primary'} onClick={fetchGif}>Gif</Button>
                        </div>
                        <div className={'post-button'}>
                            <Button type={'primary'} onClick={post}>Post</Button>
                        </div>
                    </Modal>

                    <Modal
                        visible={searchModalVisible}
                        title="Choose Gif"
                        className={'gif-modal'}
                        closeIcon={<CloseOutlined />}
                        onCancel={cancelSearch}
                        footer={false}
                    >
                        <div>
                            <div>
                                <Input
                                    className={'gif-search-bar'}
                                    autoFocus={true}
                                    type={'text'}
                                    placeholder={'Search'}
                                    onChange={searchGif}
                                    value={value}
                                />
                            </div>
                            <Divider/>
                            <div>
                                {gifLoader ?
                                    <div className="loader">
                                        <Spin/>
                                    </div>
                                    :
                                    <div className="images">
                                        {
                                            searchedGif.length ?
                                                searchedGif.map((gif, index) => (
                                                    <img
                                                        onClick={(key)=>clickedGif(key)}
                                                        key={index}
                                                        alt="Gif"
                                                        className={'gif'}
                                                        src={gif.images.fixed_height.url}
                                                    />
                                                ))
                                                :
                                                gif.map((gif, index) => (
                                                    <img
                                                        onClick={(key)=>clickedGif(key)}
                                                        key={index}
                                                        alt="Gif"
                                                        className={'gif'}
                                                        src={gif.images.fixed_height.url}
                                                    />
                                                ))
                                        }
                                    </div>
                                }
                            </div>
                        </div>
                    </Modal>
                </div>
                <div className={'post'}>
                    <Card>
                        <div>
                            <img alt="Gif" className={'post-gif'} src={"https://gw.alipayobjects.com/zos/antfincdn/LlvErxo8H9/photo-1503185912284-5271ff81b9a8.webp"}/>
                        </div>
                    </Card>
                </div>
            </Content>
        </Layout>
    );
}

export default App;
