import '../App.css';
import '../styles/style.scss';
import React, {useEffect, useState} from 'react';
import {Layout, Card, Avatar, Button, Divider, Modal, Input, Popover, Spin } from 'antd';
import {
    VideoCameraFilled,
    FileImageFilled,
    SmileFilled,
    CloseOutlined
} from '@ant-design/icons';
import { GiphyFetch } from '@giphy/js-fetch-api';

const { Header, Content } = Layout;
const { Meta } = Card;
const gf = new GiphyFetch('UWKAAuSwBXr73cXPrAna6sPY5Z1Agtby');


function App() {
    const [visible, setVisible]= useState(false),
        [gifLoader, setGifLoader]= useState(false),
        [gif, setGif]= useState([]),
        [searchedGif, setSearchedGif]= useState([]),
        [selectedGif, SetSelectedGif]= useState([]),
        [searchModalVisible, SetSearchModalVisible]= useState(false),
        [value, setValue]= useState(''),
        openModal = () => {
        setVisible(true)
        },
        handleCancel = () => {
        setVisible(false);
        SetSelectedGif([]);
        },
        fetchGif = async () => {
        SetSearchModalVisible(true);
        setGifLoader(true);
        const response= await gf.trending({ offset:20, limit: 20 });
        setGif(response.data);
        setGifLoader(false);
        },
        searchGif = async (e) => {
        setValue(e.target.value);
        setGifLoader(true);
        const response = await gf.search(`${e.target.value}`, { offset: 20, limit: 20 });
        setSearchedGif(response.data);
        setGifLoader(false);
        },
        clickedGif = (event) => {
        SetSearchModalVisible(false);
        SetSelectedGif(event.target.src);
        setValue('');
        setSearchedGif([]);
        },
        cancelSearch = () => {
        setValue('');
        setSearchedGif([]);
        SetSearchModalVisible(false)
    };
    return (
        <Layout>
            <Header className="nav-header">
                <div>
                    <strong>
                        Facebook
                    </strong>
                </div>
                <div className={'account-profile'}>
                    <Avatar src="https://joeschmoe.io/api/v1/random" />
                </div>
            </Header>
            <Content  className={'site-layout '}>
                <div className={'user-story'}>
                        <Card>
                            <img alt="example" src="https://joeschmoe.io/api/v1/random" />
                        </Card>
                        <Card>
                            <img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />
                        </Card>
                        <Card>
                            <img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />
                        </Card>
                        <Card>
                            <img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />
                        </Card>
                    {/*</div>*/}
                </div>
                <div className ={'content-post'}>
                <Card className={'post-card'}>
                    <div>
                        <Avatar src="https://joeschmoe.io/api/v1/random" />
                        <Button className={'modal-button'} onClick={openModal}> What's on your mind?</Button>
                    </div>
                    <Divider />
                    <div className={'upload-media'}>
                        <span>
                            <VideoCameraFilled />
                            <strong>
                                Live Video
                            </strong>
                        </span>
                        <span>
                            <FileImageFilled />
                             <strong>
                                 Photo/Video
                            </strong>
                        </span>
                        <span>
                            <SmileFilled />
                             <strong>
                               Feeling/Activity
                            </strong>
                        </span>
                    </div>
                </Card>
                    <Modal
                        visible={visible}
                        title="Create Post"
                        className={'post-modal'}
                        closeIcon={<CloseOutlined />}
                        onCancel={handleCancel}
                        footer={false}
                    >
                        <div>
                            <div>
                            <span>
                                <Avatar src="https://joeschmoe.io/api/v1/random" />
                                </span>
                                <span>
                             <strong>
                                 Logeshwaran
                             </strong>
                            </span>
                            </div>
                            <div>
                                <Input
                                    autoFocus={true}
                                    className={'input-field'}
                                    type='text'
                                    placeholder={'Type something here'}
                                />
                            </div>
                            {
                                selectedGif.length ?
                                    <div>
                                       <img alt="Gif" className={'post-gif'} src={selectedGif}/>
                                   </div>
                                    :
                                    (
                                        ''
                                    )
                            }
                        </div>
                        <div className={'gif-button'}>
                            <Button type={'primary'} onClick={fetchGif}>Gif</Button>
                        </div>
                    </Modal>

                    <Modal
                        visible={searchModalVisible}
                        title="Choose Gif"
                        className={'gif-modal'}
                        closeIcon={<CloseOutlined />}
                        onCancel={cancelSearch}
                        footer={false}
                    >
                        <div>
                            <div>
                                <Input
                                    className={'gif-search-bar'}
                                    autoFocus={true}
                                    type={'text'}
                                    placeholder={'Search'}
                                    onChange={searchGif}
                                    value={value}
                                />
                            </div>
                            <Divider/>
                            <div>
                                {gifLoader ?
                                    <div className="loader">
                                        <Spin/>
                                    </div>
                                    :
                                    <div className="images">
                                        {
                                            searchedGif.length ?
                                                searchedGif.map((gif, index) => (
                                                    <img
                                                        onClick={(key)=>clickedGif(key)}
                                                        key={index}
                                                        alt="Gif"
                                                        className={'gif'}
                                                        src={gif.images.fixed_height.url}
                                                    />
                                                ))
                                                :
                                                gif.map((gif, index) => (
                                                    <img
                                                        onClick={(key)=>clickedGif(key)}
                                                        key={index}
                                                        alt="Gif"
                                                        className={'gif'}
                                                        src={gif.images.fixed_height.url}
                                                    />
                                                ))
                                        }
                                    </div>
                                }
                            </div>
                        </div>
                    </Modal>
                </div>
            </Content>
        </Layout>
    );
}

export default App;
