import '../styles/style.scss';
import React, { useState } from 'react';
import {Layout, Card, Avatar, Button, Divider, Modal, Input, Popover } from 'antd';
import {
    VideoCameraFilled,
    FileImageFilled,
    SmileFilled,
    CloseOutlined
} from '@ant-design/icons';

const { Header, Content } = Layout;
const { Meta } = Card;
const content = (
    <div>
        <div>
            <div>
               <strong>
                   Choose Gif
