import '../App.css';
import '../styles/style.scss';
import React, {useEffect, useState} from 'react';
import {Layout, Card, Avatar, Button, Divider, Modal, Input, Popover, Spin } from 'antd';
import {
    VideoCameraFilled,
    FileImageFilled,
    SmileFilled,
    CloseOutlined
} from '@ant-design/icons';
import { GiphyFetch } from '@giphy/js-fetch-api';

const { Header, Content } = Layout;
const { Meta } = Card;
const gf = new GiphyFetch('UWKAAuSwBXr73cXPrAna6sPY5Z1Agtby');


function App() {
    const [visible, setVisible]= useState(false);
    const [gifLoader, setGifLoader]= useState(false);
    const [gif, setGif]= useState([]);
    const [searchedGif, setSearchedGif]= useState([]);
    const [selectedGif, SetSelectedGif]= useState([]);
    const [popoverVisible, setPopoverVisible]= useState(false);
const openModal = () => {
    setVisible(true)
};
const handleCancel = () => {
    setVisible(false);
    setPopoverVisible(false);
}
const fetchGif = async () => {
    setPopoverVisible(true);
    setGifLoader(true);
    const response= await gf.trending({ offset:20, limit: 20 });// Working
    setGif(response.data);
    setGifLoader(false);
}
const searchGif = async (e) => {
    setGifLoader(true);
    const response = await gf.search(`${e.target.value}`, { offset: 20, limit: 20 })
    setSearchedGif(response.data);
    setGifLoader(false);
}
const clickedGif = (event) => {
    setPopoverVisible(false);
    SetSelectedGif(event.target.src);
}
    const content = (
        <div>
            <div>
                <div className={'pop-over-title'}>
                    <strong>
                        Choose Gif
                    </strong>
                </div>
                <div>
                    <Input
                        className={'gif-search-bar'}
                        autoFocus={true}
                        type={'text'}
                        placeholder={'Search'}
                        onChange={searchGif}
                    />
                </div>
            </div>
            <Divider/>
            <div>
            {gifLoader ?
                <div className="example">
                    <Spin/>
                </div>
                :
                <div className="images">
                    {
                        searchedGif.length ?
                            searchedGif.map((gif, index) => (
                                    <img
                                        onClick={(key)=>clickedGif(key)}
                                        key={index}
                                        alt="Gif"
                                        className={'gif'}
                                        src={gif.images.fixed_height.url}
                                    />
                            ))
                            :
                            gif.map((gif, index) => (
                                    <img
                                        onClick={(key)=>clickedGif(key)}
                                        key={index}
                                        alt="Gif"
                                        className={'gif'}
                                        src={gif.images.fixed_height.url}
                                    />
                            ))
                    }
                </div>
            }
            </div>
        </div>
    );
    return (
        <Layout>
            <Header className="nav-header">
                <div>
                    <strong>
                        Facebook
                    </strong>
                </div>
                <div className={'account-profile'}>
                    <Avatar src="https://joeschmoe.io/api/v1/random" />
                </div>
            </Header>
            <Content  className={'site-layout '}>
                {/*<div className={'user-story'}>*/}
                {/*    <Card*/}
                {/*        hoverable*/}
                {/*        cover={<img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />}*/}
                {/*    >*/}
                {/*        /!*<Meta title="Europe Street beat" description="www.instagram.com" />*!/*/}
                {/*    </Card>*/}
                {/*    <Card*/}
                {/*        hoverable*/}
                {/*        cover={<img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />}*/}
                {/*    >*/}
                {/*        /!*<Meta title="Europe Street beat" description="www.instagram.com" />*!/*/}
                {/*    </Card>*/}
                {/*    <Card*/}
                {/*        hoverable*/}
                {/*        cover={<img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />}*/}
                {/*    >*/}
                {/*        /!*<Meta title="Europe Street beat" description="www.instagram.com" />*!/*/}
                {/*    </Card>*/}
                {/*</div>*/}
                <div className ={'content-post'}>
                <Card className={'post-card'}>
                    <div>
                        <Avatar src="https://joeschmoe.io/api/v1/random" />
                        <Button className={'modal-button'} onClick={openModal}> Type some thing here</Button>
                    </div>
                    <Divider />
                    <div className={'upload-media'}>
                        <span>
                            <VideoCameraFilled />
                            <strong>
                                Live Video
                            </strong>
                        </span>
                        <span>
                            <FileImageFilled />
                             <strong>
                                Live Video
                            </strong>
                        </span>
                        <span>
                            <SmileFilled />
                             <strong>
                                Live Video
                            </strong>
                        </span>
                    </div>
                </Card>
                    <Modal
                        visible={visible}
                        title="Create Post"
                        className={'modal'}
                        closeIcon={<CloseOutlined />}
                        onCancel={handleCancel}
                        footer={false}
                    >
                        <div>
                            <div>
                            <span>
                                <Avatar src="https://joeschmoe.io/api/v1/random" />
                                </span>
                                <span>
                             <strong>
                                 Logeshwaran
                             </strong>
                            </span>
                            </div>
                            <div>
                                <Input
                                    autoFocus={true}
                                    className={'input-field'}
                                    type='text'
                                    placeholder={'Type something here'}
                                />
                            </div>
                            {
                                selectedGif.length ?
                                    <div>
                                       <img alt="Gif" className={'post-gif'} src={selectedGif}/>
                                   </div>
                                    :
                                    (
                                        ''
                                    )
                            }
                        </div>
                        <div>
                        {/*<div className={'gif-button'}>*/}
                        {/*    <Popover*/}
                        {/*        className={'gif-drop-down'}*/}
                        {/*        style={{ width : '200px'}}*/}
                        {/*        placement="bottom"*/}
                        {/*        content={content}*/}
                        {/*        visible={popoverVisible}*/}
                        {/*        trigger="click">*/}
                                <Button type={'primary'} onClick={fetchGif}>Gif</Button>
                            {/*</Popover>*/}
                        </div>
                    </Modal>
                    <Modal
                        visible={popoverVisible}
                        title="Choose Gif"
                        className={'gif-button'}
                        closeIcon={<CloseOutlined />}
                        onCancel={handleCancel}
                        footer={false}
                    >
                        <div>
                            <div>
                                <Input className={'gif-search-bar'} autoFocus={true} type={'text'} placeholder={'Search'}/>
                            </div>
                            <Divider/>
                            <div>
                                <Card
                                    hoverable
                                    cover={<img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />}
                                />
                                <Card
                                    hoverable
                                    cover={<img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />}
                                />
                            </div>
                        </div>
                    </Modal>
                </div>
            </Content>
        </Layout>
    );
}

export default App;
