const initialState={

  userData: localStorage.getItem('userData') ? JSON.parse(localStorage.getItem('userData')) : '',
  loader: false,

}

const rootReducer= (state = {...initialState}, action) => {
    switch (action.type){
      case 'REGISTER_USER':{
      return {
          ...state, loader: true
      }
      }
      case 'REGISTER_USER_SUCCESS': {
          return {
              ...state, userData: action.payload, loader: false,
          }
      }
        case 'REGISTER_USER_FAILURE': {
            return {
                ...state, loader: false,
            }
        }
        case 'LOGIN_USER': {
            return {
                ...state, loader: true,
            }
        }
        case 'LOGIN_USER_SUCCESS': {
            return {
                ...state, userData: action.payload, loader: false,
            }
        }
        case 'LOGIN_USER_FAILURE': {
            return {
                ...state, loader: false,
            }
        }
        default:
            return state;
  }
}
export default rootReducer;
